import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './AddDoctor.css';
import DropDown from '../components/DropDown';
import { MultiSelect } from "react-multi-select-component";
import Instance from '../config/Instance';
import apiUrl from '../config/apiUrl'

const options = [
  { label: "Monday", value: "Monday" },
  { label: "Tuesday", value: "Tuesday" },
  { label: "Wednesday", value: "Wednesday" },
  { label: "Thursday", value: "Thursday" },
  { label: "Friday", value: "Friday" },
  { label: "Saturday", value: "Saturday" },
];

function AddDoctor() {
  const [selected, setSelected] = useState([]);
  const [available, setAvailable] = useState([]);
  const [name, setName] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [Doctordata, setDoctordata] = useState([]);
  const [DoctorLoginID, setDoctorLogiId] = useState([]);
  const [DoctorPassword, setDoctorpassword] = useState([]);
  useEffect(() => {
    // console.log("These days the doctor is available: ", available);
  }, [available]);

 

  useEffect(() =>{
    // console.log("This is the doctor data", Doctordata);
  },[Doctordata])

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
        const buffer = Buffer.from(reader.result); // Convert data to Buffer
        setSelectedFile(buffer);
    };
    reader.readAsArrayBuffer(file); // Read file as Array Buffer
};
const handleDoctor = () =>{
  // const user = Instance.post(apiUrl.AddDoctor, Doctordata);
  // console.log(user);
  console.log(Doctordata);
}
  return (
    <>
      <div className='mainContainer'>
        <div className='doctorAddContainer'>
          <h3 className='addTitle'>Add Doctor</h3>

          <input placeholder='Enter Name' onChange={(e) => setName(e.target.value)} />
          <input placeholder='Enter Specialization' onChange={(e) => setSpecialization(e.target.value)} />
          <input placeholder='Enter Contact Number' onChange={(e) => setContactNumber(e.target.value)} />

          <Form.Group controlId="formFile" className="mb-3">
           
            <Form.Control type="file" onChange={handleFileChange} />
          </Form.Group>

          <div className='dropdown'>
            <MultiSelect
              options={options}
              value={selected}
              onChange={setSelected}
              labelledBy="Select"
            />
          </div>
          <input placeholder='Create Login Id' onChange={(e) => setDoctorLogiId(e.target.value)} />

          <input placeholder='Create Password' type='password' onChange={(e) => setDoctorpassword(e.target.value)} />


          <Button
            variant="outline-primary"
            className='loginButton'
            onClick={() => {
             setAvailable(selected.map(day => day.value))
              const doctorData = {
                name,
                specialization,
                contactNumber,
                availability: available,
                image: selectedFile,
                DoctorLoginID,
                DoctorPassword
              };

              setDoctordata(doctorData)
              if( Doctordata.availability.length > 0){
                handleDoctor();
              }
             
            }}
          >
            Create Doctor
          </Button>
        </div>
      </div>
    </>
  );
}

export default AddDoctor;
