import React, {useState} from 'react'
import Button from 'react-bootstrap/Button';
import './AddDoctor.css'
import DropDown from '../components/DropDown';
import { MultiSelect } from "react-multi-select-component";

const options = [
  { label: "Monday", value: "Monday" },
  { label: "Tuesday", value: "Tuesday" },
  { label: "Wednesday", value: "Wednesday", },
  { label: "Thuirsday", value: "Thuirsday", },
  { label: "Friday", value: "Friday", },
  { label: "Saturday", value: "Saturday", }
];

function AddDoctor() {
  const [selected, setSelected] = useState([]);
  const [available, setAvailable] = useState([]);
  return (
    <>
<div className='mainContianer' >
<div className='doctorAddContainer'> 

<h3 className='addTitle'>Add Doctor</h3>

<input placeholder='Enter Name'/>
<input  placeholder='Enter Specialization' />
<input placeholder='Enter Contact Number' />

<div className='dropdown'> 
      <MultiSelect
        options={options}
        value={selected}
        onChange={setSelected}
        labelledBy="Select"
        
      />
    </div>

<Button variant="outline-primary" className='loginButton' onClick={() =>{
setAvailable(selected.map(day => day.value));
}} >Create Doctor</Button>
          
</div>
</div>


    </>
  )
}

export default AddDoctor
