import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Instance from '../config/Instance';
import apiUrl from '../config/apiUrl';

function ViewPatientslist() {
  const [users, setUsers] = useState(null);
  let flag = false;
  useEffect(() => {
    const getPatients = async () => {
      const response = await Instance.get(apiUrl.getPatients);
      setUsers(response.data.patients); // Assuming response.data contains the user array
    };
    getPatients();
  }, []);

  useEffect(() => {
    console.log("This is the list of users:", users);   
    flag = true;
  }, [users]);

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Location</th>
          <th>Mobile Number</th>
          <th>Last Visit</th>
          <th>Update</th>
        </tr>
      </thead>
      <tbody>
        {console.log("This is the user list from table: ", users)}
        {flag && users.map((user, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{user.firstName}</td>
            <td>{user.lastName}</td>
            <td>{user.location}</td>
            <td>{user.mobileNumber}</td>
            <td>{user.lastVisit}</td>
            <td>
              <Button variant="outline-danger">Edit</Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default ViewPatientslist;
