import React , {useState} from 'react'
import "./Login.css"
import messages from '../constants/message'
import Button from 'react-bootstrap/Button';
function Login() {
    const [currentMessageIndex, setCurrentMessageIndex] = useState(0);
    const [email, setEmail] = useState(null);
    const [password, setPassword] = useState(null);

    useEffect(() => {
        const intervalId = setInterval(() => {
          setCurrentMessageIndex((prevIndex) => (prevIndex + 1) % Object.keys(messages).length);
        }, 1000);
    
        // Clean up the interval on component unmount
        return () => clearInterval(intervalId);
      }, []);
  return (
  <>
  
  <div className='adminMainContainer'>

<div className='imageSignUp'>
    <img src="https://res.cloudinary.com/practicaldev/image/fetch/s--O0u1bNHs--/c_limit%2Cf_auto%2Cfl_progressive%2Cq_66%2Cw_880/https://miro.medium.com/max/1400/0%2APXf5ge7QCN9Ga_CL.gif"/>
    <p>Don't have an account? <a href='/signup'>Signup</a></p>
</div>
<div className='loginQuotes'></div>
        <h3 className='title'>Hospital Management App</h3>
          <h3 className='login'>Login</h3>
          <input placeholder='Email' type='email' onChange={(e) => setEmail(e.target.value)} />
          <input placeholder='password' type='password' onChange={(e) => setPassword(e.target.value)} />
          <Button variant="outline-primary" className='loginButton' >Login</Button>
          <p className='footer'>{Object.values(messages)[currentMessageIndex]}</p>
          <hr className='horizontalRow'/>
  </div>

  </>
  )
}

export default Login
